mapper.select:
		SELECT * FROM (SELECT ROWNUM RNUM, PAGING.* 
		FROM(
			SELECT BNUM, CATEGORY, BTITLE, MNUM, BDATE, BCONTENT, MNAME ,MID, REPLY, BCOUNT, MPICTURE, (CASE WHEN CATEGORY='공지사항' THEN '1' ELSE '0' END) CATE
			FROM BOARD JOIN MEMBER USING(MNUM)
		
		
			
				
					
						BTITLE LIKE #{pattern} OR BCONTENT LIKE #{pattern} OR MNAME LIKE #{pattern}
					
					
						BTITLE LIKE #{pattern} OR BCONTENT LIKE #{pattern}
					
					
						MNAME LIKE #{pattern}
					
				
			
		
		ORDER BY CATE DESC, BDATE DESC) PAGING) 
        WHERE RNUM BETWEEN #{startRow} AND #{endRow}

SELECT COUNT(*) FROM (SELECT b.*, m.* , (CASE WHEN CATEGORY='공지사항' THEN '1' ELSE '0' END) CATE 
FROM BOARD B JOIN MEMBER M ON(B.MNUM=M.MNUM) 


	
		
			
				BTITLE LIKE #{pattern} OR BCONTENT LIKE #{pattern} OR MNAME LIKE #{pattern}
			
			
				BTITLE LIKE #{pattern} OR BCONTENT LIKE #{pattern}
			
			
				MNAME LIKE #{pattern}
			
		
	

ORDER BY CATE DESC, BDATE DESC)
